export ANDROID_NDK_HOME="$ANDROID_SDK_HOME/ndk/$ANDROID_NDK_VERSION"
export FFMPEG_INCLUDE_DIR_AARCH64_LINUX_ANDROID="$JNI_INCLUDE_DIR/arm64-v8a/"
