export FFMPEG_INCLUDE_DIR_ARMV7_LINUX_ANDROIDEABI="$JNI_INCLUDE_DIR/armeabi-v7a/"
export FFMPEG_LIB_DIR_ARMV7_LINUX_ANDROIDEABI="$JNI_LIB_DIR/armeabi-v7a/"

export FFMPEG_INCLUDE_DIR_I686_LINUX_ANDROID="$JNI_INCLUDE_DIR/x86/"
export FFMPEG_LIB_DIR_I686_LINUX_ANDROID="$JNI_LIB_DIR/x86/"